$(function () {
    const reveal = () => {
        $('.fade-in').each(function () {
            const top = $(this).offset().top;
            const view = $(window).scrollTop() + $(window).height();
            if (top < view - 80) $(this).addClass('visible');
        });
    };
    reveal();
    $(window).on('scroll', reveal);
    $('#year').text(new Date().getFullYear());
});

